'use strict';
//@@DocBrief("The base class for all exceptions")
class CtdtrlException {
    code;
    message;
    getMessage() {
        return this.message;
    }
    getCode() {
        return this.code;
    }
    static create(code, message) {
        let ex;
        ex = new exception();
        ex.code = code;
        ex.message = message;
        return ex;
    }
}
let exception = CtdtrlException;
module.exports = exception
