'use strict';
class DXFeedConnectionState {
    static get DISCONNECTED() { return 0; }
    static get CONNECTING() { return 1; }
    static get CONNECTED() { return 2; }
    state;
    error;
    constructor() {
        this.state = DXFeedConnectionState.DISCONNECTED;
        this.error = null;
    }
    isConnected() {
        return this.state == DXFeedConnectionState.CONNECTED;
    }
    isDisconnected() {
        return this.state == DXFeedConnectionState.DISCONNECTED;
    }
    isConnecting() {
        return this.state == DXFeedConnectionState.CONNECTING;
    }
    hasError() {
        return this.error != null;
    }
    getError() {
        return this.error;
    }
    setState(state) {
        this.state = state;
        this.error = null;
    }
    setStateWithError(state, error) {
        this.state = state;
        this.error = error;
    }
}
module.exports = DXFeedConnectionState
