'use strict';
//@@DocBrief("The class for checking the current state of data manager")
class DataManagerState {
    static get NOT_LOADED() { return 0; }
    static get LOADING() { return 1; }
    static get LOADED() { return 2; }
    state;
    error;
    constructor() {
        this.state = DataManagerState.NOT_LOADED;
        this.error = null;
    }
    isNotLoaded() {
        return this.state == DataManagerState.NOT_LOADED;
    }
    isLoading() {
        return this.state == DataManagerState.LOADING;
    }
    isLoaded() {
        return this.state == DataManagerState.LOADED;
    }
    hasError() {
        return this.error != null;
    }
    getError() {
        return this.error;
    }
    setState(state) {
        this.state = state;
        this.error = null;
    }
    setStateWithError(state, error) {
        this.state = state;
        this.error = error;
    }
}
module.exports = DataManagerState
