'use strict';
let BaseEvent = require('./BaseEvent.js');
//@@DocInclude
//@@DocBrief("This class provides methods to get the error message and create a new object with an error message")
class ErrorEvent extends BaseEvent {
    errorMessage;
    constructor() {
        super();
        this.errorMessage = "";
    }
    getErrorMessage() {
        return this.errorMessage;
    }
    static create(name, eventDate, errorMessage) {
        let instance;
        instance = new ErrorEvent();
        instance.errorMessage = errorMessage;
        instance.name = name;
        instance.eventDate = eventDate;
        return instance;
    }
}
module.exports = ErrorEvent
