'use strict';
let stdlib = require('@gehtsoft/LuaxStdlib-node').stdlib;
//@@DocBrief("Escapes XML special characters")
class EscapeXmlUtil {
    static escape(source) {
        if (source != null && EscapeXmlUtil.hasMarkupSymbols(source)) {
            return "<![CDATA[" + source + "]]>";
        }
        return source;
    }
    static hasMarkupSymbols(source) {
        if (stdlib.len(source) == 0) {
            return false;
        }
        return stdlib.indexOf(source, "\\", false) >= 0 || stdlib.indexOf(source, "<", false) >= 0 || stdlib.indexOf(source, ">", false) >= 0 || stdlib.indexOf(source, "&", false) >= 0;
    }
}
module.exports = EscapeXmlUtil
