'use strict';
class EventIdGenerator {
    static instance = null;
    static counter = 0;
    static getGenerator() {
        if (EventIdGenerator.instance == null) {
            EventIdGenerator.instance = new EventIdGenerator();
            EventIdGenerator.counter = 0;
        }
        return EventIdGenerator.instance;
    }
    getNextId() {
        let id;
        id = (EventIdGenerator.counter).toString();
        EventIdGenerator.counter = EventIdGenerator.counter + 1;
        return id;
    }
}
module.exports = EventIdGenerator
