'use strict';
let EventLoggerWrapper = require('./EventLoggerWrapper.js');
let LogManager = require('./LogManager.js');
let exception = require('@gehtsoft/LuaxStdlib-node').exception;
let FXConnectLiteErrorCodes = require('./FXConnectLiteErrorCodes.js');
class EventManager {
    static instance = null;
    static getLogger() {
        if (EventManager.instance == null) {
            EventManager.instance = new EventLoggerWrapper();
        }
        return EventManager.instance;
    }
    static setLogger(logger) {
        if (logger == null) {
            LogManager.getLogger().error("EventManager.setLogger(): logger is not set");
            throw exception.create(FXConnectLiteErrorCodes.CODE_REQUIRED_PARAMETER_NOT_SET, "logger is not set");
        }
        if (EventManager.instance == null) {
            EventManager.instance = new EventLoggerWrapper();
        }
        EventManager.instance.setLogger(logger);
    }
}
module.exports = EventManager
