'use strict';
let ExecutionTimeEventBuilder = require('./ExecutionTimeEventBuilder.js');
let EventManager = require('./EventManager.js');
class ExecutionTimeEvenUtil {
    static eventStart(executionTimeEventName) {
        let eventRefresh;
        eventRefresh = ExecutionTimeEventBuilder.create(executionTimeEventName);
        eventRefresh.start();
        return eventRefresh;
    }
    static eventStop(eventRefresh) {
        eventRefresh.stop();
        EventManager.getLogger().performance(eventRefresh.build());
    }
    static eventStopWithError(eventRefresh, error) {
        eventRefresh.stopWithError(error);
    }
}
module.exports = ExecutionTimeEvenUtil
