'use strict';
let BaseEvent = require('./BaseEvent.js');
//@@DocInclude
//@@DocBrief("This class helps to track the execution time of a specific event or function in a program")
class ExecutionTimeEvent extends BaseEvent {
    duration;
    constructor() {
        super();
        this.duration = 0;
    }
    getDuration() {
        return this.duration;
    }
    static create(name, eventDate, duration) {
        let instance;
        instance = new ExecutionTimeEvent();
        instance.duration = duration;
        instance.name = name;
        instance.eventDate = eventDate;
        return instance;
    }
}
module.exports = ExecutionTimeEvent
