'use strict';
let xmlNode = require('@gehtsoft/LuaxStdlib-node').xmlNode;
let XmlReaderUtil = require('./XmlReaderUtil.js');
class FxmsgReaderUtil {
    static getMessageNode(xmlRootNode, messageType) {
        let i, child;
        if (xmlRootNode != null && xmlRootNode.getChildrenCount() > 0) {
            for (i = 0; i <= xmlRootNode.getChildrenCount() - 1; i += 1) {
                child = xmlRootNode.getChild(i);
                if (child.getType() == xmlNode.ELEMENT && child.getName() == "m" && XmlReaderUtil.isAttrEqualTo(child, "t", messageType)) {
                    return child;
                }
            }
        }
        return null;
    }
}
module.exports = FxmsgReaderUtil
