'use strict';
class HostsParserXmlUtils {
    static findNodeByName(name, root) {
        let node, attr, i;
        for (i = 0; i <= root.getChildrenCount() - 1; i += 1) {
            node = root.getChild(i);
            attr = node.getAttributeByName("name");
            if (attr != null) {
                if (attr.getValue() == name) {
                    return node;
                }
            }
        }
        return null;
    }
    static getAttrStringValue(nodeInner, attrName) {
        let attr;
        attr = nodeInner.getAttributeByName(attrName);
        if (attr != null) {
            return attr.getValue();
        }
        return "";
    }
}
module.exports = HostsParserXmlUtils
