'use strict';
let InstrumentUpdateMessage = require('./InstrumentUpdateMessage.js');
let UpdateCommandType = require('./UpdateCommandType.js');
class InstrumentUpdateMessageBuilder extends InstrumentUpdateMessage {
    setInstrument(instrument) {
        this.instrument = instrument;
    }
    setUpdateCommand(updateCommand) {
        if (updateCommand == "U") {
            this.updateCommand = UpdateCommandType.UPDATE;
        } else if (updateCommand == "I") {
            this.updateCommand = UpdateCommandType.INSERT;
        } else if (updateCommand == "D") {
            this.updateCommand = UpdateCommandType.DELETE;
        }         else {
            this.updateCommand = UpdateCommandType.UNKNOWN;
        }
    }
    build() {
        return this;
    }
}
module.exports = InstrumentUpdateMessageBuilder
