'use strict';
let DefaultLogger = require('./DefaultLogger.js');
//@@DocInclude
//@@DocBrief("The logger factory")
class LoggerFactory {
    static get LEVEL_DEBUG() { return "debug"; }
    static get LEVEL_INFO() { return "info"; }
    static get LEVEL_WARNING() { return "warning"; }
    static get LEVEL_ERROR() { return "error"; }
    static get LEVEL_FATAL() { return "fatal"; }
    static createLogger(level) {
        let result;
        result = new DefaultLogger();
        result.setLevel(level);
        return result;
    }
}
module.exports = LoggerFactory
