'use strict';
let LogManager = require('./LogManager.js');
let exception = require('@gehtsoft/LuaxStdlib-node').exception;
let FXConnectLiteErrorCodes = require('./FXConnectLiteErrorCodes.js');
class MarginPercentHedgeCalculator {
    static calculate(marginPercentHedge, hedgeFlag) {
        if (hedgeFlag == null) {
            LogManager.getLogger().error("MarginPercentHedgeCalculator.calculate(): hedgeFlag is not set");
            throw exception.create(FXConnectLiteErrorCodes.CODE_REQUIRED_PARAMETER_NOT_SET, "hedgeFlag is not set");
        }
        if (hedgeFlag == "Y") {
            return marginPercentHedge;
        }
        return 0;
    }
}
module.exports = MarginPercentHedgeCalculator
