'use strict';
//@@DocBrief("Class declares a set of functions to compute common mathematical operations and transformations")
class MathUtil {
    static maxInt(a, b) {
        if (a > b) {
            return a;
        }
        else {
            return b;
        }
    }
    static minInt(a, b) {
        if (a < b) {
            return a;
        }
        else {
            return b;
        }
    }
    static pow(base, exponent) {
        let result, i;
        result = 1;
        for (i = 1; i <= exponent; i += 1) {
            result = result * base;
        }
        return result;
    }
}
module.exports = MathUtil
