'use strict';
let PdasMessageFieldTag = require('./PdasMessageFieldTag.js');
let PdasMessageValue = require('./PdasMessageValue.js');
let LockedMessageBuilder = require('./LockedMessageBuilder.js');
class MessageBuilder {
    static getOfferIdsGroup(factory, offerIds) {
        let group, plain, i;
        plain = "";
        if (offerIds.length > 0) {
            plain = offerIds[0];
            for (i = 1; i <= offerIds.length - 1; i += 1) {
                plain = plain + "," + offerIds[i];
            }
        }
        group = factory.createGroup();
        group.addChild(factory.createStringField(PdasMessageFieldTag.FXCM_PARAM_NAME, PdasMessageValue.CLIENT_OFFER_IDS));
        group.addChild(factory.createStringField(PdasMessageFieldTag.FXCM_PARAM_VALUE, plain));
        return group;
    }
    static createLockedMessage(message) {
        let lockedMessageBuilder;
        lockedMessageBuilder = new LockedMessageBuilder();
        lockedMessageBuilder.setMessage(message);
        return lockedMessageBuilder.build();
    }
}
module.exports = MessageBuilder
