'use strict';
let OfferUpdateMessage = require('./OfferUpdateMessage.js');
let UpdateCommandType = require('./UpdateCommandType.js');
class OfferUpdateMessageBuilder extends OfferUpdateMessage {
    setOffer(offer) {
        this.offer = offer;
    }
    setUpdateCommand(updateCommand) {
        if (updateCommand == "U") {
            this.updateCommand = UpdateCommandType.UPDATE;
        } else if (updateCommand == "I") {
            this.updateCommand = UpdateCommandType.INSERT;
        } else if (updateCommand == "D") {
            this.updateCommand = UpdateCommandType.DELETE;
        } else if (updateCommand == "UI") {
            this.updateCommand = UpdateCommandType.UPDATE_OR_INSERT;
        }         else {
            this.updateCommand = UpdateCommandType.UNKNOWN;
        }
    }
    build() {
        return this;
    }
}
module.exports = OfferUpdateMessageBuilder
