'use strict';
class OpenPositionsDirectionsData {
    buyLots;
    sellLots;
    constructor() {
        this.buyLots = 0;
        this.sellLots = 0;
    }
    getAllBuyLots() {
        return this.buyLots;
    }
    getAllSellLots() {
        return this.sellLots;
    }
    addLots(lots, isBuy) {
        if (isBuy) {
            this.buyLots = this.buyLots + lots;
        }
        else {
            this.sellLots = this.sellLots + lots;
        }
    }
}
module.exports = OpenPositionsDirectionsData
