'use strict';
//@@DocBrief("Builder of an ordered unique string")
class OrderedStringBuilder {
    static counter = 0;
    static instance = null;
    static getInstance() {
        if (OrderedStringBuilder.instance == null) {
            OrderedStringBuilder.instance = new OrderedStringBuilder();
            OrderedStringBuilder.counter = 0;
        }
        return OrderedStringBuilder.instance;
    }
    next() {
        OrderedStringBuilder.counter = OrderedStringBuilder.counter + 1;
        return (OrderedStringBuilder.counter).toString();
    }
}
module.exports = OrderedStringBuilder
