'use strict';
class OrdersUtil {
    static getEntryOrderType(entryOrderRequest, bid, ask) {
        let type;
        if (entryOrderRequest.getBuySell() == "B" || entryOrderRequest.getBuySell() == "b") {
            if (entryOrderRequest.getRate() > ask) {
                type = "SE";
            }
            else {
                type = "LE";
            }
        }
        else {
            if (entryOrderRequest.getRate() < bid) {
                type = "SE";
            }
            else {
                type = "LE";
            }
        }
        return type;
    }
    static getStopEntryOrderSide(buySell) {
        if ("B" == buySell || "b" == buySell) {
            return "1";
        }
        else {
            return "-1";
        }
    }
    static getLimitEntryOrderSide(buySell) {
        if ("B" == buySell || "b" == buySell) {
            return "-1";
        }
        else {
            return "1";
        }
    }
    static getClientRate(buySell, opposite, bid, ask) {
        if ((buySell == "B" || buySell == "b") && !opposite || buySell != "B" && buySell != "b" && opposite) {
            return ask;
        }
        else {
            return bid;
        }
    }
}
module.exports = OrdersUtil
