'use strict';
let stdlib = require('@gehtsoft/LuaxStdlib-node').stdlib;
let EscapeXmlUtil = require('./EscapeXmlUtil.js');
class PdasMessageField {
    tag;
    value;
    type;
    getName() {
        return this.tag;
    }
    getValue() {
        return this.value;
    }
    getType() {
        return this.type;
    }
    serialize() {
        let result;
        result = "<f n=\"" + this.tag + "\"";
        if (this.type != null && stdlib.len(this.type) != 0) {
            result = result + " t=\"" + this.type + "\"";
        }
        result = result + ">" + EscapeXmlUtil.escape(this.value) + "</f>";
        return result;
    }
    getElementType() {
        return "f";
    }
}
module.exports = PdasMessageField
