'use strict';
let PdasMessageField = require('./PdasMessageField.js');
class PdasMessageFieldBuilder extends PdasMessageField {
    buildString(tag, value) {
        this.tag = tag;
        this.value = value;
        this.type = "";
        return this;
    }
    buildInt(tag, value) {
        this.tag = tag;
        this.value = (value).toString();
        this.type = "i";
        return this;
    }
    buildLong(tag, value) {
        this.tag = tag;
        this.value = (value).toString();
        this.type = "l";
        return this;
    }
    buildDouble(tag, value) {
        this.tag = tag;
        this.value = (value).toString();
        this.type = "d";
        return this;
    }
}
module.exports = PdasMessageFieldBuilder
