'use strict';
let exception = require('@gehtsoft/LuaxStdlib-node').exception;
let stdlib = require('@gehtsoft/LuaxStdlib-node').stdlib;
class PriceHistoryValidator {
    static validateParameters(instrument, timeframe, from, to, isTo, callback) {
        if (callback == null) {
            throw exception.create(0, "callback is not set in 'getPrices' method call");
        }
        if (instrument == null || stdlib.len(instrument) == 0) {
            return "Instrument is not set";
        }
        if (timeframe == null) {
            return "Timeframe is not set";
        }
        if (isTo) {
            if (stdlib.toJdn(from) >= stdlib.toJdn(to)) {
                return "'From' date should be less than 'To' date";
            }
        }
        return null;
    }
}
module.exports = PriceHistoryValidator
