'use strict';
let Resilience = require('./Resilience.js');
//@@DocInclude
//@@DocBrief("The builder of the Resilience class")
class ResilienceBuilder extends Resilience {
    setPeriodInMilliseconds(periodInMilliseconds) {
        this.periodInMilliseconds = periodInMilliseconds;
    }
    setAttempts(attempts) {
        this.attempts = attempts;
    }
    build() {
        return this;
    }
    copyFrom(resilience) {
        this.setPeriodInMilliseconds(resilience.getPeriodInMilliseconds());
        this.setAttempts(resilience.getAttempts());
    }
}
module.exports = ResilienceBuilder
