'use strict';
class SocketCommunicatorState {
    static get CLOSED() { return 0; }
    static get OPENING() { return 1; }
    static get OPEN() { return 2; }
    state;
    error;
    constructor() {
        this.state = 0;
        this.error = null;
    }
    setState(state) {
        this.state = state;
        this.error = null;
    }
    setStateWithError(state, error) {
        this.state = state;
        this.error = error;
    }
    isOpen() {
        return this.state == SocketCommunicatorState.OPEN;
    }
    isOpening() {
        return this.state == SocketCommunicatorState.OPENING;
    }
    isClosed() {
        return this.state == SocketCommunicatorState.CLOSED;
    }
    hasError() {
        return this.error != null;
    }
    getError() {
        return this.error;
    }
}
module.exports = SocketCommunicatorState
