'use strict';
let FXConnectLiteErrorBuilder = require('./FXConnectLiteErrorBuilder.js');
class StateStatusUtils {
    static notifyStateChangeWithError(state, stateChangePublisher, stateStatus, error, logger, managerName) {
        let errorBuilder;
        logger.error(managerName + ". State status changed: " + (stateStatus).toString() + " with error " + error);
        errorBuilder = new FXConnectLiteErrorBuilder();
        errorBuilder.setMessage(error);
        state.setStateWithError(stateStatus, errorBuilder);
        stateChangePublisher.notifyStateChange(state);
    }
}
module.exports = StateStatusUtils
