'use strict';
let BaseSLOrderRequest = require('./BaseSLOrderRequest.js');
let TrailingStopType = require('./TrailingStopType.js');
//@@DocInclude
//@@DocBrief("Represents a stop order request that can be sent to a trading platform")
class StopOrderRequest extends BaseSLOrderRequest {
    trailingType;
    trailingStep;
    constructor() {
        super();
        this.trailingType = TrailingStopType.None;
        this.trailingStep = 0;
    }
    isEnabledTrailing() {
        return this.trailingType != TrailingStopType.None;
    }
    getTrailingType() {
        return this.trailingType;
    }
    getTrailingStep() {
        return this.trailingStep;
    }
}
module.exports = StopOrderRequest
