'use strict';
let stdlib = require('@gehtsoft/LuaxStdlib-node').stdlib;
class SymbolNameGenerator {
    static get PairCombinationMajor() { return "/"; }
    static generate(currency1, currency2) {
        if (currency1 == null) {
            if (currency2 == null) {
                return "";
            }
            else {
                return currency2;
            }
        }
        if (currency2 == null) {
            return currency1;
        }
        if (stdlib.len(currency1) == 0) {
            return currency2;
        }
        if (stdlib.len(currency2) == 0) {
            return currency1;
        }
        return currency1 + SymbolNameGenerator.PairCombinationMajor + currency2;
    }
}
module.exports = SymbolNameGenerator
