'use strict';
let stdlib = require('@gehtsoft/LuaxStdlib-node').stdlib;
//@@DocBrief("Class for processing time stamps")
class TimeStamp {
    dt;
    getTimeStampString() {
        let result;
        result = this.getFilledString(stdlib.year(this.dt), 4) + this.getFilledString(stdlib.month(this.dt), 2) + this.getFilledString(stdlib.day(this.dt), 2) + "-";
        result = result + this.getFilledString(stdlib.hour(this.dt), 2) + ":";
        result = result + this.getFilledString(stdlib.minute(this.dt), 2) + ":";
        result = result + this.getFilledString(stdlib.second(this.dt), 2) + ".";
        result = result + this.getFilledString(stdlib.millisecond(this.dt), 3);
        return result;
    }
    static create(dt) {
        let instance;
        instance = new TimeStamp();
        instance.dt = dt;
        return instance;
    }
    getFilledString(value, length) {
        let temp;
        temp = (value).toString();
        while(stdlib.len(temp) < length) {
            temp = "0" + temp;
        }
        return temp;
    }
}
module.exports = TimeStamp
