'use strict';
let TimeframeUnit = require('./TimeframeUnit.js');
let exception = require('@gehtsoft/LuaxStdlib-node').exception;
//@@DocInclude
//@@DocBrief("The class of the time frames")
class Timeframe {
    size;
    unit;
    constructor() {
        this.size = 0;
        this.unit = TimeframeUnit.Unknown;
    }
    getSize() {
        return this.size;
    }
    getUnit() {
        return this.unit;
    }
    static create(unit, size) {
        let timeframe;
        if (unit < TimeframeUnit.Minute || unit > TimeframeUnit.Tick) {
            throw exception.create(0, "Unknow timeframe unit: " + (unit).toString());
        }
        timeframe = new Timeframe();
        timeframe.unit = unit;
        timeframe.size = size;
        return timeframe;
    }
    toString() {
        return (this.size).toString() + TimeframeUnit.toString(this.unit);
    }
}
module.exports = Timeframe
