'use strict';
let TimeframeUnit = require('./TimeframeUnit.js');
class TimeframeUtils {
    static get JDN_FRACTION() { return 86400; }
    static getJdnIncrement(timeframe) {
        let incrementJdn;
        incrementJdn = 0;
        if (timeframe.getUnit() == TimeframeUnit.Minute) {
            incrementJdn = 1;
        } else if (timeframe.getUnit() == TimeframeUnit.Hour) {
            incrementJdn = 60;
        } else if (timeframe.getUnit() == TimeframeUnit.Day) {
            incrementJdn = 1440;
        } else if (timeframe.getUnit() == TimeframeUnit.Week) {
            incrementJdn = 10080;
        } else if (timeframe.getUnit() == TimeframeUnit.Month) {
            incrementJdn = 43200;
        } else if (timeframe.getUnit() == TimeframeUnit.Year) {
            incrementJdn = 525600;
        }
        return incrementJdn * timeframe.getSize() * 60 / TimeframeUtils.JDN_FRACTION;
    }
    static whetherSuppressTime(timeframe) {
        let unit;
        unit = timeframe.getUnit();
        return unit == TimeframeUnit.Day || unit == TimeframeUnit.Week || unit == TimeframeUnit.Month || unit == TimeframeUnit.Year;
    }
}
module.exports = TimeframeUtils
