'use strict';
let stdlib = require('@gehtsoft/LuaxStdlib-node').stdlib;
//@@DocBrief("Builder of an unique string")
class UniqueStringBuilder {
    static getString() {
        let tempString, index;
        tempString = (stdlib.toJdn(stdlib.nowlocal())).toString();
        index = stdlib.indexOf(tempString, ".", false);
        if (index >= 0) {
            tempString = stdlib.substring(tempString, 0, index) + stdlib.substring(tempString, index + 1, stdlib.len(tempString) - index - 1);
        }
        index = stdlib.indexOf(tempString, ",", false);
        if (index >= 0) {
            tempString = stdlib.substring(tempString, 0, index) + stdlib.substring(tempString, index + 1, stdlib.len(tempString) - index - 1);
        }
        return tempString;
    }
}
module.exports = UniqueStringBuilder
