'use strict';
let xmlNode = require('@gehtsoft/LuaxStdlib-node').xmlNode;
class XmlReaderUtil {
    static isAttrEqualTo(node, attrName, expectedValue) {
        let attr;
        attr = node.getAttributeByName(attrName);
        return attr != null && attr.getValue() == expectedValue;
    }
    static getAttrValue(node, attrName) {
        let attr;
        attr = node.getAttributeByName(attrName);
        if (attr != null) {
            return attr.getValue();
        }
        return null;
    }
    static getNodeText(node) {
        let i, child, response;
        response = "";
        for (i = 0; i <= node.getChildrenCount() - 1; i += 1) {
            child = node.getChild(i);
            if (child.getType() == xmlNode.TEXT || child.getType() == xmlNode.CDATA) {
                response = response + child.getValue();
            }
        }
        return response;
    }
}
module.exports = XmlReaderUtil
